import React from 'react';
import { Grid } from '@mui/material';
import { useStyles } from './Styles';

const Platform = () => {
    const classes = useStyles();
    const products = [
        {
            title: "Living/ Dining Room",
            icon: <i className="fa fa-house" style={{color:"gray", fontSize:"35px"}}></i>,
            details: "TV unit, TV Back Paneling Bar Unit, Book shelf Showcase",
        },
        {
            title: "Bedroom",
            icon: <i className="fa-light fa-house-laptop"></i>,
            details: "TWardrobes, TV unit, Storage Bed,Dressing unit, Study Unit,Kids bunker beds.",
        },
        {
            title: "Kitchen",
            icon: <i className="fa-solid fa-kitchen-set"></i>,
            details: "Counter tops, Storages Paneling, accessories,Pantry pull out",
        },
        {
            title: "Interior Design",
            icon: <i className="fa-light fa-house-laptop"></i>,
            details: "Wall Paneling, Lighting also Ceiling, civil work furnishing, Appliances",
        },
        {
            title: "Home Refurbish",
            icon: <i className="fa-regular fa-house"></i>,
            details: "Kitchen/ bathroom refurbish Tilling, plumbing, electrical Painting, Deep clean",
        },
    ]
    return (
        <div>
            <Grid container item xs={12} className={classes.root}>
                <Grid item xs={2}>
                    <div style={{ fontSize: "40px", fontWeight: "600" }}>360</div>
                </Grid>
                <Grid item xs={10} className={classes.DesignContainer}>
                    {
                        products.map((product) => (
                            <Grid className={classes.boxDiv}>
                                <div>
                                    {product.icon}
                                </div>
                                <div>
                                    <p>{product.title}</p>
                                    <p style={{margin:""}}>{product.details}</p>
                                </div>
                            </Grid>
                        ))
                    }

                </Grid>
            </Grid>
        </div>
    );
};

export default Platform;
