import { makeStyles } from '@mui/styles';


export const useStyles = makeStyles({
 
  menuPart:{
    // width:"400px",
    display:"flex",
    justifyContent:"space-between",
  },
  displayBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"300px",
    height:"300px",
    backgroundColor:"gray"
    
  },
  bigBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"200px",
    height:"200px",
    backgroundColor:"gray"
    
  },
  smallBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"100px",
    height:"100px",
    backgroundColor:"gray"
    
  }


});
