import { makeStyles } from '@mui/styles';


export const useStyles = makeStyles({
 
  menuPart:{
    // width:"400px",
    display:"flex",
    justifyContent:"space-between",
    marginLeft:"150px"
  },
 
  smallBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"100px",
    height:"115px",
    backgroundColor:"gray",
    margin:"20px"
    
  },
  mainCart:{
    backgroundColor: "white", height: "100px", width: "100%" 
  },
  yellowCart:{
    backgroundColor:'yellow',
    width:"200px",
    height:"80px",
    margin:"10px",
    padding:"10px",
  },
  pText:{
    fontSize:"12px",
    color:"gray",
    textAlign:"left"
  },
  HeadText:{
    color:"gray",
    textAlign:"left",
    fontWeight:""
  }


});
