import { makeStyles } from '@mui/styles';


export const useStyles = makeStyles({
 
  menuPart:{
    display:"flex",
    justifyContent:"space-between",
    marginLeft:"150px"
  },
 
  mainCart:{
    backgroundColor: "#a7a0a0", height: "100px", width: "100%" ,
    marginTop:"20px",
    marginBottom:"20px"
  },
 
  pText:{
    fontSize:"12px",
    color:"#a7a0a0",
    textAlign:"left"
  },
  HeadText:{
    color:"#a7a0a0",
    textAlign:"left",
    fontWeight:"600",
    margin:"0px"
  }


});
