import { makeStyles } from '@mui/styles';


export const useStyles = makeStyles({
 
  menuPart:{
    // width:"400px",
    display:"flex",
    justifyContent:"center",
  },
  displayBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"500px",
    height:"300px",
    // backgroundColor:"gray",
    margin:"20px"
    
  },
  bigBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"200px",
    height:"115px",
    backgroundColor:"gray",
    margin:"20px"
    
  },
  smallBox:{
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
    width:"100px",
    height:"115px",
    backgroundColor:"gray",
    margin:"20px"
    
  }


});
